include("Mach3.js");

/**
 * Configuration for Mach3 controllers.
 * Output in Inches.
 */
function Mach3IN(documentInterface, camDocumentInterface) {
    Mach3.call(this, documentInterface, camDocumentInterface);

    this.decimals = 4;
    this.unit = RS.Inch;

    // G20: Inch unit
    // G17: XY Plane select
    // G90: Absolute distance mode
    // G40: Cancel cutter radius compensation
    // G49: Cancel tool length offset
    // G80: Cancel motion mode
    // G70: Inch unit
    // G91.1: Incremental distance mode for I/J
    this.header = [
        "[N] G20 G17 G90 G40 G49 G80",
        "[N] G70 G91.1"
    ];
}

Mach3IN.prototype = new Mach3();
Mach3IN.displayName = "Mach3 (G41/G42) [in]";
